import React, { useState } from "react";
import "./ProductSection.css"; // Ensure this CSS file contains necessary styles

const ProductSection = () => {
  const [selectedCategory, setSelectedCategory] = useState("featured");
  const [quickViewProduct, setQuickViewProduct] = useState(null);

  const productData = {
    featured: [
      { id: 1, name: "Couple Ring", image: "/images/product/1.jpg", price: "₹44,015" },
      { id: 2, name: "Necklace Set", image: "/images/product/1.jpg", price: "₹45,999" },
    ],
    newArrivals: [
      { id: 3, name: "Earrings", image: "images/insta-3.jpg", price: "₹30,000" },
      { id: 4, name: "Bangles", image: "images/insta-4.jpg", price: "₹35,000" },
    ],
    onSale: [
      { id: 5, name: "Wedding Set", image: "images/insta-5.jpg", price: "₹50,000" },
    ],
  };

  const handleCategoryChange = (category) => setSelectedCategory(category);

  const handleQuickView = (product) => setQuickViewProduct(product);

  const closeQuickView = () => setQuickViewProduct(null);

  return (
    <div className="product-section">
      {/* Category Tabs */}
      <div className="tabs">
        <button onClick={() => handleCategoryChange("featured")}>Featured</button>
        <button onClick={() => handleCategoryChange("newArrivals")}>New Arrivals</button>
        <button onClick={() => handleCategoryChange("onSale")}>On Sale</button>
      </div>

      {/* Product Grid */}
      <div className="product-grid">
        {productData[selectedCategory].map((product) => (
          <div key={product.id} className="product-card">
            <img src={product.image} alt={product.name} />
            <h3>{product.name}</h3>
            <p>{product.price}</p>
            <button onClick={() => handleQuickView(product)}>Quick View</button>
          </div>
        ))}
      </div>

      {/* Quick View Modal */}
      {quickViewProduct && (
        <div className="quick-view-modal">
          <div className="quick-view-content">
            <h2>{quickViewProduct.name}</h2>
            <img src={quickViewProduct.image} alt={quickViewProduct.name} />
            <p>Price: {quickViewProduct.price}</p>
            <button onClick={closeQuickView}>Close</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default ProductSection;
