import React from "react";
import SlickSlider from "react-slick"; // Renaming to avoid confusion
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

function CustomSlider() {
  // Slider data
  const slides = [
    {
      id: 1,
      image: "/images/slider/1.png",
      title: "Necklace",
      description: "22 Carat Gold Necklace for Wedding",
      price: "Starting at Rs. 75,999",
    },
    {
      id: 2,
      image: "/images/slider/2.jpg",
      title: "Earrings and Pendant",
      description: "Complete bridal set with white pearls",
      price: "Starting at Rs. 89,499",
    },
  ];

  // Slider settings for react-slick
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 3000,
  };

  return (
    <div className="slider_area">
      <SlickSlider {...settings}>
        {slides.map((slide) => (
          <div key={slide.id} className="single_slider">
            <div
              className="slider_content"
              style={{
                backgroundImage: `url(${slide.image})`,
                backgroundSize: "cover",
                backgroundPosition: "center",
                height: "100vh",
                display: "flex",
                alignItems: "center",
                justifyContent: "center",
                color: "#fff",
                textAlign: "center",
              }}
            >
              <div>
                <h1>{slide.title}</h1>
                <p>{slide.description}</p>
                <span>{slide.price}</span>
              </div>
            </div>
          </div>
        ))}
      </SlickSlider>
    </div>
  );
}

export default CustomSlider;
