import React from "react";
import ReactDOM from "react-dom/client";
import App from "./App";
import "./style.css";

// ✅ Import Bootstrap CSS & JS properly
import "bootstrap/dist/css/bootstrap.min.css";
import "bootstrap/dist/js/bootstrap.bundle.min.js";

// ✅ Import Carousel & Animation Libraries
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import "owl.carousel/dist/assets/owl.carousel.css";
import "owl.carousel/dist/assets/owl.theme.default.css";

// ✅ Import only necessary JS libraries
import "slick-carousel";
import "owl.carousel";

// ✅ Ensure WOW.js is initialized correctly
import { WOW } from "wowjs";

// ✅ Create Root Element
const rootElement = document.getElementById("root");

// ✅ Ensure DOM is fully loaded before initializing WOW.js
if (rootElement) {
  new WOW().init();
  const root = ReactDOM.createRoot(rootElement);
  root.render(<App />);
} else {
  console.error("Root element not found! Ensure index.html has a <div id='root'></div>.");
}
