import React from "react";
import "../style.css"; // Ensure your styles are linked

const ProductSection1 = ({ selectedCategory }) => {
  const products = [
    { id: 1, name: "Gold Earrings", category: "Earrings", price: "₹45015", image: "/images/product/1.jpg" },
    { id: 2, name: "Diamond Pendant", category: "Pendants", price: "₹55015", image: "/images/product/2.jpg" },
    { id: 3, name: "Silver Ring", category: "Rings", price: "₹35015", image: "/images/product/3.jpg" },
    { id: 4, name: "Gold Chain", category: "Chains", price: "₹65015", image: "/images/product/4.jpg" },
    { id: 5, name: "Men’s Bracelet", category: "Bracelets", price: "₹75015", image: "/images/product/5.jpg" },
    { id: 6, name: "Gemstone Ring", category: "Gemstones", price: "₹85015", image: "/images/product/6.jpg" },
    { id: 7, name: "Platinum Chain", category: "Platinum", price: "₹95015", image: "/images/product/7.jpg" },
    { id: 8, name: "Silver Coin", category: "Coins", price: "₹15015", image: "/images/product/8.jpg" },
  ];

  const filteredProducts =
    selectedCategory === "All"
      ? products
      : products.filter((product) => product.category === selectedCategory);

  return (
    <section className="product_section">
      <div className="container">
        <h2>Products - {selectedCategory}</h2>
        <div className="product-grid">
          {filteredProducts.map((product) => (
            <div key={product.id} className="product-card">
              <img src={product.image} alt={product.name} />
              <h3>{product.name}</h3>
              <p className="price">{product.price}</p>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default ProductSection1;
