import React from "react";
import "owl.carousel/dist/assets/owl.carousel.css"; // Owl Carousel styles
import "owl.carousel/dist/assets/owl.theme.default.css";
import OwlCarousel from "react-owl-carousel";

function Slider() {
  // Slider data
  const slides = [
    {
      id: 1,
      backgroundImage: "/images/slider/1.png", // Path to your first image in the public folder
      offer: "Exclusive Offer -20% Off This Week",
      title: "Necklace",
      description: "22 Carat Gold Necklace for Wedding",
      price: "Starting at Rs. 75,999",
      buttonText: "Shop Now",
    },
    {
      id: 2,
      backgroundImage: "/images/slider/2.jpg", // Path to your second image in the public folder
      offer: "Exclusive Offer -40% Off This Week",
      title: "Earrings and Pendant",
      description: "Complete bridal set with white pearls",
      price: "Starting at Rs. 89,499",
      buttonText: "Shop Now",
    },
    {
      id: 3,
      backgroundImage: "/images/slider/3.jpg", // Path to your third image in the public folder
      offer: "Exclusive Offer -10% Off This Week",
      title: "Wedding Rings",
      description: "Ashirwaad Special Wedding Rings for Couples",
      price: "Starting at Rs. 14,999",
      buttonText: "Shop Now",
    },
  ];

  // Owl Carousel options
  const carouselOptions = {
    items: 1,
    loop: true,
    autoplay: true,
    autoplayTimeout: 6000,
    animateOut: "fadeOut",
    dots: true,
    nav: false,
  };

  return (
    <div className="slider_area">
      <OwlCarousel className="owl-theme" {...carouselOptions}>
        {slides.map((slide) => (
          <div
            key={slide.id}
            className="single_slider"
            style={{
              backgroundImage: `url(${slide.backgroundImage})`,
              backgroundSize: "cover",
              backgroundPosition: "center",
              height: "100vh",
              display: "flex",
              alignItems: "center",
              justifyContent: "center",
            }}
          >
            <div className="container">
              <div className="slider_content" style={{ textAlign: "center", color: "#fff" }}>
                <p>{slide.offer}</p>
                <h1>{slide.title}</h1>
                <span>{slide.description}</span>
                <p className="slider_price">{slide.price}</p>
                <a href="#" className="button">
                  {slide.buttonText}
                </a>
              </div>
            </div>
          </div>
        ))}
      </OwlCarousel>
    </div>
  );
}

export default Slider;
