import React, { useState } from "react";
import "./ProductSection.css";

const products = {
  featured: [
    { id: 1, name: "Necklace", image: "/images/product/1.jpg", price: "₹44015", description: "Elegant gold-plated necklace.", rating: 4 },
    { id: 2, name: "Earrings", image: "/images/product/2.jpg", price: "₹45015", description: "Stylish diamond earrings.", rating: 5 },
  ],
  arrivals: [
    { id: 13, name: "Bracelet", image: "/images/product/13.jpg", price: "₹74015", description: "Classic charm bracelet.", rating: 4 },
    { id: 14, name: "Bangles", image: "/images/product/14.jpg", price: "₹64015", description: "Elegant gold bangles set.", rating: 3 },
  ],
  onsale: [
    { id: 25, name: "Ring", image: "/images/product/25.jpg", price: "₹54015", description: "Stylish gemstone ring.", rating: 5 },
    { id: 26, name: "Diamonds", image: "/images/product/26.jpg", price: "₹94015", description: "Premium diamond collection.", rating: 4 },
  ],
};

const ProductSection = () => {
  const [modalProduct, setModalProduct] = useState(null);

  const handleQuickView = (product) => {
    setModalProduct(product);
  };

  const renderStars = (rating) => {
    return Array(5)
      .fill(0)
      .map((_, i) => (
        <span key={i} className={`star ${i < rating ? "filled" : ""}`}>
          ★
        </span>
      ));
  };

  return (
    <section className="product-section">
      <div className="container">
        <h2>Our Collection</h2>

        <ul className="nav nav-tabs">
          <li className="nav-item">
            <a className="nav-link active" data-bs-toggle="tab" href="#featured">
              Featured
            </a>
          </li>
          <li className="nav-item">
            <a className="nav-link" data-bs-toggle="tab" href="#arrivals">
              New Arrivals
            </a>
          </li>
          <li className="nav-item">
            <a className="nav-link" data-bs-toggle="tab" href="#onsale">
              On Sale
            </a>
          </li>
        </ul>

        <div className="tab-content mt-4">
          {["featured", "arrivals", "onsale"].map((category) => (
            <div
              key={category}
              className={`tab-pane fade ${category === "featured" ? "show active" : ""}`}
              id={category}
            >
              <div className="product-grid">
                {products[category].map((product) => (
                  <div key={product.id} className="product-card">
                    <div className="image-container">
                      <img src={product.image} alt={product.name} />
                      <div className="hover-info">
                        <h3>{product.name}</h3>
                        <p className="price">{product.price}</p>
                        <button
                          className="quick-view-btn"
                          onClick={() => handleQuickView(product)}
                        >
                          Quick View
                        </button>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          ))}
        </div>

        {/* Modal */}
        {modalProduct && (
          <div
            className="modal fade show d-block"
            style={{ backgroundColor: "rgba(0, 0, 0, 0.5)" }}
          >
            <div className="modal-dialog">
              <div className="modal-content">
                <div className="modal-header">
                  <h5 className="modal-title">{modalProduct.name}</h5>
                  <button
                    type="button"
                    className="btn-close"
                    onClick={() => setModalProduct(null)}
                  ></button>
                </div>
                <div className="modal-body">
                  <img
                    src={modalProduct.image}
                    alt={modalProduct.name}
                    className="img-fluid"
                  />
                  <p className="product-description">{modalProduct.description}</p>
                  <p className="price">Price: {modalProduct.price}</p>
                  <div className="rating">{renderStars(modalProduct.rating)}</div>
                  <div className="modal-buttons">
                    <button className="btn btn-secondary">Add to Wishlist</button>
                    <button className="btn btn-warning">Compare</button>
                    <button className="btn btn-primary">Add to Cart</button>
                  </div>
                </div>
              </div>
            </div>
          </div>
        )}
      </div>
    </section>
  );
};

export default ProductSection;
