import React, { useState } from "react";
import "./ProductSection.css"; // Assuming you have custom styles

const ProductSection = () => {
  const [modalProduct, setModalProduct] = useState(null);

  const products = {
    featured: [
      { id: 1, name: "Necklace", image: "/images/product/1.jpg", price: "₹44015" },
      { id: 2, name: "Earrings", image: "/images/product/2.jpg", price: "₹45015" },
    ],
    arrivals: [
      { id: 3, name: "Bracelet", image: "/images/product/3.jpg", price: "₹74015" },
      { id: 4, name: "Bangles", image: "/images/product/4.jpg", price: "₹64015" },
    ],
    onSale: [
      { id: 5, name: "Ring", image: "/images/product/5.jpg", price: "₹54015" },
      { id: 6, name: "Diamonds", image: "/images/product/6.jpg", price: "₹94015" },
    ],
  };

  const handleQuickView = (product) => {
    setModalProduct(product);
  };

  return (
    <section className="product_section p_section1 product_black_section">
      <div className="container">
        <ul className="nav nav-tabs">
          <li className="nav-item">
            <a className="nav-link active" data-bs-toggle="tab" href="#featured">
              Featured
            </a>
          </li>
          <li className="nav-item">
            <a className="nav-link" data-bs-toggle="tab" href="#arrivals">
              New Arrivals
            </a>
          </li>
          <li className="nav-item">
            <a className="nav-link" data-bs-toggle="tab" href="#onsale">
              On Sale
            </a>
          </li>
        </ul>

        <div className="tab-content mt-4">
          {["featured", "arrivals", "onSale"].map((category) => (
            <div
              key={category}
              className={`tab-pane fade ${
                category === "featured" ? "show active" : ""
              }`}
              id={category}
            >
              <div className="row">
                {products[category].map((product) => (
                  <div key={product.id} className="col-md-3 mb-4">
                    <div className="product-card">
                      <img src={product.image} alt={product.name} className="img-fluid" />
                      <h5>{product.name}</h5>
                      <p>{product.price}</p>
                      <button
                        className="btn btn-primary"
                        onClick={() => handleQuickView(product)}
                      >
                        Quick View
                      </button>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          ))}
        </div>

        {/* Modal */}
        {modalProduct && (
          <div className="modal fade show d-block" style={{ backgroundColor: 'rgba(0, 0, 0, 0.5)' }}>
            <div className="modal-dialog">
              <div className="modal-content">
                <div className="modal-header">
                  <h5 className="modal-title">{modalProduct.name}</h5>
                  <button
                    type="button"
                    className="btn-close"
                    onClick={() => setModalProduct(null)}
                  ></button>
                </div>
                <div className="modal-body">
                  <img
                    src={modalProduct.image}
                    alt={modalProduct.name}
                    className="img-fluid"
                  />
                  <p>{modalProduct.price}</p>
                </div>
              </div>
            </div>
          </div>
        )}
      </div>
    </section>
  );
};

export default ProductSection;
