import React, { useState } from "react";
import "./ProductSection.css";

const ProductSection = () => {
  const [selectedCategory, setSelectedCategory] = useState("featured");

  const products = {
    featured: [
      { id: 1, name: "Couple Ring", image: "images/insta-1.jpg", price: "₹44,015", description: "This is a gold ring with diamond." },
      { id: 2, name: "Necklace Set", image: "images/insta-2.jpg", price: "₹45,999", description: "Beautiful traditional necklace." },
    ],
    newArrivals: [
      { id: 3, name: "Earrings", image: "images/insta-3.jpg", price: "₹30,000", description: "Elegant gold earrings." },
    ],
    onSale: [
      { id: 4, name: "Wedding Set", image: "images/insta-5.jpg", price: "₹50,000", description: "Stunning bridal jewelry set." },
    ],
  };

  const handleCategoryChange = (category) => setSelectedCategory(category);

  return (
    <div className="product-section">
      {/* Tabs */}
      <div className="tabs">
        <button onClick={() => handleCategoryChange("featured")}>Featured</button>
        <button onClick={() => handleCategoryChange("newArrivals")}>New Arrivals</button>
        <button onClick={() => handleCategoryChange("onSale")}>On Sale</button>
      </div>

      {/* Product Grid */}
      <div className="product-grid">
        {products[selectedCategory].map((product) => (
          <div key={product.id} className="product-card">
            <div className="image-container">
              <img src={product.image} alt={product.name} />
              <div className="hover-info">
                <h3>{product.name}</h3>
                <p>{product.description}</p>
                <p className="price">{product.price}</p>
                <button className="quick-view-btn">Quick View</button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProductSection;
