import React, { useState, useEffect } from "react";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Navigate,
} from "react-router-dom";
import Home from "./components/Home";
import Login from "./components/Login";
import Register from "./components/Register";
import Header from "./components/Header";
import Slider from "./components/Slider";
import Banner from "./components/Banner";
import ProductSection from "./components/ProductSection";
import Footer from "./components/Footer";
import Banner1 from "./components/Banner1";
import ProductSection1 from "./components/ProductSection1";
import Blog from "./components/Blog";
import Instagram from "./components/Instagram";
import Subscribe from "./components/Subscribe";
import Banner2 from "./components/Banner2";
import Modal from "./components/Modal";

const App = () => {
  const [user, setUser] = useState(() => {
    // Load user from localStorage when the app starts
    return JSON.parse(localStorage.getItem("user")) || null;
  });

  const [selectedCategory, setSelectedCategory] = useState("All");

  useEffect(() => {
    localStorage.setItem("user", JSON.stringify(user)); // Persist user on login/logout
  }, [user]);

  return (
    <Router>
      {/* Header remains constant for all pages */}
      <Header setSelectedCategory={setSelectedCategory} />

      <Routes>
        {/* Home Page - Always accessible */}
        <Route
          path="/"
          element={
            <>
              <Home />
              <Slider />
              <Banner />
              <ProductSection />
              <Banner1 />
              <Blog />
              <Instagram />
              <Subscribe />
              <Banner2 />
              <Footer />
              <Modal />
            </>
          }
        />

        {/* Login & Register Pages */}
        <Route path="/login" element={<Login setUser={setUser} />} />
        <Route path="/register" element={<Register setUser={setUser} />} />

        {/* Protected Route - User Must Be Logged In */}
        <Route
          path="/dashboard"
          element={
            user ? (
              <>
                <Slider />
                <Banner />
                <ProductSection />
                <Banner1 />
                <Blog />
                <Instagram />
                <Subscribe />
                <Banner2 />
                <Footer />
                <Modal />
              </>
            ) : (
              <Navigate to="/" />
            )
          }
        />

        {/* Category-based Product Page */}
        <Route
          path="/products"
          element={<ProductSection1 selectedCategory={selectedCategory} />}
        />

        {/* Blog Page */}
        <Route path="/blog" element={<Blog />} />
      </Routes>
    </Router>
  );
};

export default App;
