import React, { useState } from "react";
import "./ProductSection.css";

const ProductSection = () => {
  const [modalProduct, setModalProduct] = useState(null);

  const products = {
    featured: [
      { id: 1, name: "Couple Ring", image: "/images/product1.jpg", price: "₹44,015", description: "This is a gold ring with diamond." },
      { id: 2, name: "Necklace Set", image: "/images/product2.jpg", price: "₹45,999", description: "Beautiful traditional necklace." },
    ],
    arrivals: [
      { id: 3, name: "Earrings", image: "/images/product3.jpg", price: "₹30,000", description: "Elegant gold earrings." },
      { id: 4, name: "Wedding Set", image: "/images/product4.jpg", price: "₹50,000", description: "Stunning bridal jewelry set." },
    ],
  };

  const handleQuickView = (product) => {
    setModalProduct(product);
  };

  return (
    <section className="product-section">
      <div className="container">
        <h2>Our Collection</h2>

        <ul className="nav nav-tabs">
          <li className="nav-item">
            <a className="nav-link active" data-bs-toggle="tab" href="#featured">
              Featured
            </a>
          </li>
          <li className="nav-item">
            <a className="nav-link" data-bs-toggle="tab" href="#arrivals">
              New Arrivals
            </a>
          </li>
        </ul>

        <div className="tab-content mt-4">
          {["featured", "arrivals"].map((category) => (
            <div
              key={category}
              className={`tab-pane fade ${category === "featured" ? "show active" : ""}`}
              id={category}
            >
              <div className="product-grid">
                {products[category].map((product) => (
                  <div key={product.id} className="product-card">
                    <div className="image-container">
                      <img src={product.image} alt={product.name} />
                      <div className="hover-info">
                        <h3>{product.name}</h3>
                        <p>{product.description}</p>
                        <p className="price">{product.price}</p>
                        <button className="quick-view-btn" onClick={() => handleQuickView(product)}>
                          Quick View
                        </button>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          ))}
        </div>

        {/* Modal */}
        {modalProduct && (
          <div className="modal fade show d-block" style={{ backgroundColor: 'rgba(0, 0, 0, 0.5)' }}>
            <div className="modal-dialog">
              <div className="modal-content">
                <div className="modal-header">
                  <h5 className="modal-title">{modalProduct.name}</h5>
                  <button
                    type="button"
                    className="btn-close"
                    onClick={() => setModalProduct(null)}
                  ></button>
                </div>
                <div className="modal-body">
                  <img src={modalProduct.image} alt={modalProduct.name} className="img-fluid" />
                  <p>{modalProduct.price}</p>
                </div>
              </div>
            </div>
          </div>
        )}
      </div>
    </section>
  );
};

export default ProductSection;
