import React from "react";
import OwlCarousel from "react-owl-carousel";
import "owl.carousel/dist/assets/owl.carousel.css";
import "owl.carousel/dist/assets/owl.theme.default.css";

const Blog = () => {
  const blogPosts = [
    {
      image: "/images/blog/4.jpg",
      title: "Earrings for Navratri",
      author: "Ashirwaad",
      date: "4 November 2020",
      description: "Lorem ipsum dolor sit amet consectetur adipisicing elit. Sunt fugit commodi quo eligendi laudantium quisquam, magnam hic numquam fuga illum sed aperiam sint, expedita dolor.",
      link: "#",
    },
    {
      image: "/images/blog/1.jpg",
      title: "Pendant for Engagement",
      author: "Ashirwaad",
      date: "3 November 2020",
      description: "Lorem ipsum dolor sit amet consectetur adipisicing elit. Sunt fugit commodi quo eligendi laudantium quisquam, magnam hic numquam fuga illum sed aperiam sint, expedita dolor.",
      link: "#",
    },
    {
      image: "/images/blog/2.jpg",
      title: "Engagement Couple Rings",
      author: "Ashirwaad",
      date: "2 November 2020",
      description: "Lorem ipsum dolor sit amet consectetur adipisicing elit. Sunt fugit commodi quo eligendi laudantium quisquam, magnam hic numquam fuga illum sed aperiam sint, expedita dolor.",
      link: "#",
    },
    {
      image: "/images/blog/3.jpg",
      title: "Earrings for Party",
      author: "Ashirwaad",
      date: "5 November 2020",
      description: "Lorem ipsum dolor sit amet consectetur adipisicing elit. Sunt fugit commodi quo eligendi laudantium quisquam, magnam hic numquam fuga illum sed aperiam sint, expedita dolor.",
      link: "#",
    },
  ];

  return (
    <section className="blog_section blog_black">
      <div className="container">
        <div className="row">
          <div className="col-12">
            <div className="section_title">
              <h2>Ashirwaad Updates</h2>
            </div>
          </div>
        </div>
        <div className="row">
          <OwlCarousel
            className="owl-theme"
            loop
            margin={10}
            nav
            dots={true}
            autoplay={true}
            autoplayTimeout={3000}
            responsive={{
              0: { items: 1 },
              768: { items: 2 },
              1024: { items: 3 },
            }}
          >
            {blogPosts.map((post, index) => (
              <div className="single_blog" key={index}>
                <div className="blog_thumb">
                  <a href={post.link}>
                    <img src={post.image} alt={post.title} />
                  </a>
                </div>
                <div className="blog_content">
                  <h3>
                    <a href={post.link}>{post.title}</a>
                  </h3>
                  <div className="author_name">
                    <p>
                      <span>by</span>
                      <span className="themes"> {post.author} </span>
                      <span className="post_by"> / {post.date} </span>
                    </p>
                  </div>
                  <div className="post_desc">
                    <p>{post.description}</p>
                  </div>
                  <div className="read_more">
                    <a href={post.link}>Continue Reading</a>
                  </div>
                </div>
              </div>
            ))}
          </OwlCarousel>
        </div>
      </div>
    </section>
  );
};

export default Blog;
